DROP TABLE IF EXISTS district_records;
CREATE TABLE IF NOT EXISTS district_records(
  id INTEGER(11) NOT NULL PRIMARY KEY 
, district_name VARCHAR(255)
, total_houses INTEGER(11)
, total_population INTEGER(11)
, death INTEGER(11)
, injured INTEGER(1)
, govt_houses_full_destroyed INTEGER(11)
, govt_houses_partial_destroyed INTEGER(11)
, public_house_full_destroyed INTEGER(11)
, public_house_partial_destroyed INTEGER(11)
, displaced_male INTEGER(11) 
, displaced_female INTEGER(11) 
, property_loss VARCHAR(255)
, Incident_Place VARCHAR(255)
, no_of_displaced_family INTEGER(11) 
, cattles_loss INTEGER(11) 
, office VARCHAR(255)
, remarks VARCHAR(255)
);
